            uint256 mulReduce,
            bool bidFlag
        )
    {
        (
            amount,
            onePrice,
            remainTime,
            mulReduce,
            bidFlag,
            ,

        ) = _getCurrSaleInfo(auctionId);
    }

    function getCurrSaleInfoV2(
        uint256 auctionId
    )
        external
        view
        returns (
            uint256 amount,
            uint256 onePrice,
            uint256 remainTime,
            uint256 mulReduce,
            bool bidFlag,
            bool harvestFlag,
